import React, { useState } from "react";
import Cropper from "react-easy-crop";

function ImageCropper({ image, onCropDone, onCropCancel }) {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedArea, setCroppedArea] = useState(null);
  const [aspectRatio, setAspectRatio] = useState(3/4);

  const onCropComplete = (croppedAreaPercentage, croppedAreaPixels) => {
    setCroppedArea(croppedAreaPixels);
  };

  const onAspectRatioChange = (event) => {
    setAspectRatio(event.target.value);
  };

  return (
    <div className="cropper">
      <div>
        <Cropper
          image={image}
          aspect={aspectRatio}
          crop={crop}
          zoom={zoom}
          onAspectRatioChange={setAspectRatio}
          onCropChange={setCrop}
          onZoomChange={setZoom}
          onCropComplete={onCropComplete}
          style={{
            containerStyle: {
              width: "30%",
              height: "30%",
              backgroundColor: "#fff",
            },
          }}
        />
      </div>

      <div className="action-btns">
        <div className="input-text" color='#777777' onChange={onAspectRatioChange}>
          <input type="radio" value={1/1} name="ratio" /> 1:1
          <input type="radio" value={3/4} name="ratio" /> 3:4
          <input type="radio" value={9/16} name="ratio" /> 9:16
        </div>
        <div  className="input-text">
        Zoom<input type="range" min={1} max={3} step={0.1} value={zoom} color='#777777' onChange={(e) => setZoom(e.target.value)} class="slider" id="zoom"/>
        </div>
        <button className="button-light" onClick={onCropCancel}>Cancel</button>
        <button className="button-light" onClick={() => {onCropDone(croppedArea);}}>Done</button>
      </div>
    </div>
  );
}

export default ImageCropper;
