        <div className='auth-form-container'>
            <form className='login-form' onSubmit={handleSubmit}>
            <label className='h1'>DreamVideo</label>
                <form>
                    <label className='h2-light'>Login</label>
                    <label className='h2-dark'>Register</label>
                </form>
                <label className='input-title' for='user'>Name</label>
