                <label className='input-title' for='password'>Password</label>
                <input className='input-box' value={user} type='password' placeholder='******' id='password' name='password' />
                <button className='button-dark' type='submit'>Log In</button>
                <button className='h2-dark' onClick={() => props.onFormSwitch('register')}>Don't have an account? Register</button>
            </form>
        </div>
    );
};

export default Login;
