import { Link as RouterLink, useNavigate} from 'react-router-dom';
import React, { useRef } from "react";
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';
import map from "lodash/map";
import range from "lodash/range";
import {ScrollBox, ScrollAxes, FastTrack} from 'react-scroll-box';


const Account = (props) => {
    const items=['Item 1','Item 2','Item 3','Item 4'];

    const navigate = useNavigate();

    const inputRef = useRef();
    const handleOnChange = (event) => {
        if (event.target.files && event.target.files.length > 0) {
          const reader = new FileReader();
          reader.readAsDataURL(event.target.files[0]);
          reader.onload = function (e) {
            onImageSelected(reader.result);
          };
        }
      };

      const onChooseImg = () => {
        inputRef.current.click();
      };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert('output'+e);
    }
    function handleScenes(e, itemId) {
        const newScenes = scenes.map((s,i) => {if(i===itemId){return e;}else{return s;}});
        setScenes(newScenes);
    }
    function handleChars(e, itemId, charId) {
        const newChars = [];
        for (let i=0;i<items.length;i++) {
            if (i === itemId) newChars.push( chars[i].map((s,i) => {if(i===charId){return e;}else{return s;}}));
            else newChars.push( chars[i].map((s,i) => {return s;}));
        }
        setChars(newChars);
    }
    function handleActs(e, itemId, charId) {
        const newActs = [];
        for (let i=0;i<items.length;i++) {
            if (i === itemId) newActs.push( acts[i].map((s,i) => {if(i===charId){return e;}else{return s;}}));
            else newActs.push( acts[i].map((s,i) => {return s;}));
        }
        setActs(newActs);
    }

    const DreamCard = ({itemId}) => {
        //alert('id:'+id.target);
        //const itemId = '0';

        return (
            <div className='column' style={{minHeight:'60px', width:'30%', textAlign:'left',}}>
                <form className='dream-file' >
                    <text className='input-title' style={{textAlign:'left'}}>Dream Title{itemId}</text>
                    <text className='input-text' style={{textAlign:'left', marginTop:'10px', fontSize:'18px'}}>Dream description...</text>
                </form>
            </div>
        );
    }

    const CharCard = ({itemId}) => {
        return (
            <div class='row' style={{textAlign:'center', }}>
                <form className='dream-file' style={{height:'110px', width:'110px'}}>
                </form>
                <text className='input-title' style={{color:'#FFFFFF'}}>Char {itemId}</text>
            </div>
        )
    }

    return (
        <div className='input-container' style={{textAlign:'left'}}>
            <div className='column' style={{width:'50%', textAlign:'left', }} onSubmit={handleSubmit}>
                <div className='header' style={{position:'fixed', width:'1100px'}} >
                    <label className='h3-light'>Want to have a new dream journey?</label>
                    <button className='button-light' onClick={() => navigate("/description")} style={{position: 'relative', left: '70px', top: '120px'}}>Start</button>
                </div>

                <div class='inte-form' style={{height:'500px', width:'500px', marginTop:'250px', marginBottom:'100px', overflow:'scroll'}}>
                <div class='row' style={{textAlign:'left', }}>
                    {items.map((item, index) => (

                        <div className='column' style={{minHeight:'60px', width:'50%', textAlign:'left',}}>{DreamCard( {itemId:index}) }</div>

                    ))}
                     </div>
                </div>
            </div>

            <div  className='column' style={{width:'50%', textAlign:'right', transform:'translate(9%, 0)'}} onSubmit={handleSubmit}>
            <div class='inte-form' style={{background: 'rgba(0,0,0, 0.5)', height:'700px', width:'500px', marginTop:'50px', marginBottom:'100px', overflow:'scroll'}}>
                <div class='row'>
                    {items.map((item, index) => (
                        <div className='column' style={{minHeight:'60px', width:'33%', textAlign:'left',}}>{CharCard( {itemId:index}) }</div>
                    ))}
                </div>

                <div>
                    <input
                        type="file"
                        accept="/*"
                        ref={inputRef}
                        onChange={handleOnChange}
                        style={{ display: "none" }}
                    />

                    <button className="btn" onClick={onChooseImg}>
                        Choose Image
                    </button>
                    </div>

                <button className='button-light' style={{position: 'relative', top:'30%'}}>Add</button>
            </div>
            </div>
        </div>
    );
};

export default Account;

