import { Link as RouterLink, useNavigate } from 'react-router-dom';
import React, { useState } from "react";
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

const Description = (props) => {
    const [style, setStyle] = useState('');
    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');
    //const navigate = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(e.target.value);
    }

    return (
        <div className='input-container' style={{textAlign:'left'}}>
            <label className='h3-light'>Please describe your dream.</label>
            <form className='desc-form' onSubmit={handleSubmit}>
                <div class='row'>
                    <div class='column'>
                    <label className='input-title'>Dream title.</label>
                    <input className='input-box' value={title} onChange={e => setTitle(e.target.value)} type='text' placeholder='' id='title' name='title' />
                    </div>
                    <div class='column'>
                    <label className='input-title'>Dream style.</label>
                    <input className='input-box' value={style} onChange={e => setStyle(e.target.value)} type='text' placeholder='' id='style' name='style' />
                    </div>
                </div>
                <label className='input-title'>Dream Description.</label>
                <textarea className='input-box-large' value={desc} onChange={e => setDesc(e.target.value.replace('\\n', '\n'))} rows='10' cols='50' placeholder='' id='desc' name='desc' />
                <button className='button-dark' type='submit' style={{position: 'relative', left: '85%', top: '-90px'}}>Save</button>
            </form>
        </div>
    );
};

export default Description;
