import { Link, useNavigate } from 'react-router-dom';
import React, { useState } from "react";
var connect_config = require('../config.json');

const Description = (props) => {
    const [style, setStyle] = useState('real photo high resolution');
    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        var serverUrl = connect_config.server_host+'/description/'
        fetch(serverUrl, {
            method: 'POST',
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Methods": "*",
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({'user':window.sessionStorage.getItem('user_name'), 
                'title':title, 'style':style, 'desc':desc})
          })
          .then(response => response.json())
          .then(response => {
            window.sessionStorage.setItem('dream_desc', response);
            if (response.res === false) {alert('[Error] '+response.message);}
            else {navigate('/intepretation');}
          })
    }

    return (
        <div className='input-container' style={{textAlign:'left'}} onSubmit={handleSubmit}>
            <label className='h3-light'>Please describe your dream.</label>
                <button className='button-dark' type='submit' onClick={() => navigate("/account")} style={{position: 'relative', left: '70px', top: '120px'}}>Back</button>
                <button className='button-dark' type='submit' onClick={() => navigate("/interpretation")} style={{position: 'relative', left: '70px', top: '120px', marginLeft:'30px'}}>Save</button>
            <form className='desc-form' style={{marginTop:'160px', marginBottom:'120px'}} >
                <body>
                <div class='row'>
                    <div class='column' style={{width:'64%'}}>
                    <label className='input-title'>Dream title.</label>
                    <input className='input-box' value={title} style={{width:'640px'}} onChange={e => setTitle(e.target.value)} type='text' placeholder='' id='title' name='title' />
                    </div>
                    <div class='column' style={{width:'30%'}}>
                    <label className='input-title' style={{marginLeft:'60px'}}>Dream style.</label>
                    <select className='dropdown' value={style} style={{marginLeft:'60px'}} onChange={e => setStyle(e.target.value)} id='style' name='style'>
                        <option value='real photo high resolution'>Real photo</option>
                        <option value='artistic'>Artistic</option>
                    </select>
                    </div>
                </div>
                </body>
                <label className='input-title' style={{marginTop:'20px'}}>Dream Description.</label>
                <label className='input-text' style={{marginTop:'10px', marginBottom:'5px'}}>Please specify the name of each character and seperate each sense using '.'</label>
                <textarea className='input-box-large' value={desc} onChange={e => setDesc(e.target.value.replace('\\n', '\n'))} rows='5' cols='50' placeholder='' id='desc' name='desc' />
                {//<button className='button-dark' type='submit' style={{position: 'relative', left: '85%', top: '-90px'}}>Save</button>
                }
            </form>
        </div>
    );
};

export default Description;
