import React, { useState } from "react";
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

const Sound = (props) => {
    const [regen, setRegen] = useState('');
    const navigate = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(e.target.value);
    }

    return (
        <div className='input-container' style={{textAlign:'left'}}>
            <label className='h3-light'>Background Music and Narration</label>


            <button className='button-light' type='submit' onClick={() => navigate("/interpretation")} style={{position: 'relative', left: '70px', top: '120px'}}>Back</button>
            <button className='button-light' type='submit' style={{position: 'relative', left: '70px', top: '120px', marginLeft:'30px'}}>Save</button>
            <button className='button-light' type='submit' style={{position: 'relative', left: '70px', top: '120px', marginLeft:'30px'}}>Regenerate</button>
            <form className='desc-form' style={{height: '500px'}} onSubmit={handleSubmit}>
            <text className='input-title' style={{textAlign:'left', marginTop:'30px'}}>Do you want a generative background music?</text>
            <text className='input-title' style={{textAlign:'left', marginTop:'30px'}}>This might take minutes, feel free to log out and come back again for your dream video in the dream cloister.</text>

            </form>
        </div>
    );
};

export default Sound;
