import { Link as RouterLink, useNavigate } from 'react-router-dom';
import React, { useRef, useLayoutEffect, useState } from "react";
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';
import map from "lodash/map";
import range from "lodash/range";
import {ScrollBox, ScrollAxes, FastTrack} from 'react-scroll-box';


const elemPrefix = "test";
const getId = (index: number) => `${elemPrefix}${index}`;

const getItems = () =>
  Array(5)
    .fill(0)
    .map((_, ind) => ({ id: getId(ind) }));

const Interpretation = (props) => {

    const stickyHeader = useRef()
    useLayoutEffect(() => {
        const mainHeader = document.getElementById('mainHeader')
        let fixedTop = stickyHeader.current.offsetTop
        const fixedHeader = () => {
        if (window.pageYOffset > fixedTop) {
            mainHeader.classList.add('fixedTop')
        } else {
            mainHeader.classList.remove('fixedTop')
        }
        }
        window.addEventListener('scroll', fixedHeader)
    }, [])

    const [items] = React.useState(getItems);
    const [sentences, setSentences] = useState(['s1','s2','s3','s4'])
    const [chars, setChars] = useState(['s1','s2','s3','s4']);
    const [scenes, setScenes] = useState(['s1','s2','s3','s4']);
    const [acts, setActs] = useState(['s1','s2','s3','s4']);
    //const navigate = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        alert('output'+e);
    }

    const SceneCard = (id) => {
        //alert('id:'+id.target);
        const itemId = '0';
        return (
            <div>
                <form className='desc-form' onSubmit={handleSubmit}>
                    <text className='input-text' style={{textAlign:'left'}}>{sentences[itemId]}</text>
                    <button className='button-dark' style={{position: 'relative', left: '85%', top: '-35px'}}>Merge</button>
                </form>

                <form className='inte-form'style={{marginTop:'40px'}}>
                    <body>
                    <div class='row'>
                        <div class='column' style={{width:'30%'}}>
                        <label className='input-title'>Character.</label>
                        <input className='input-box' value={chars[itemId]} style={{width:'300px'}} onChange={e => setChars(e.target.value[itemId])} type='text' placeholder='' id='char' name='char' />
                        </div>
                        <div class='column' style={{width:'70%'}}>
                        <label className='input-title' style={{marginLeft:'60px'}}>Action.</label>
                        <input className='input-box' value={acts[itemId]} style={{marginLeft:'60px', width:'630px'}} onChange={e => setActs(e.target.value[itemId])} type='text' placeholder='' id='act' name='act' />
                        </div>
                    </div>
                    </body>
                    <label className='input-title' style={{marginTop:'10px'}}>Scene.</label>
                    <textarea className='input-box-large' value={scenes[itemId]} style={{height:'100px'}} onChange={e => setScenes(e.target.value[itemId])} rows='5' cols='50' placeholder='' id='scene' name='scene' />
                </form>

            </div>
        );
    }

    return (
        <div className='input-container' style={{textAlign:'left'}}>
            <div className='header' ref={stickyHeader} style={{position:'fixed', alignSelf: 'flex-start'}} >
                <label className='h3-light'>Information and relationship interpretation.</label>
                <button className='button-light' type='submit' style={{position: 'relative', left: '70px', top: '120px'}}>Save</button>
            </div>

            <ScrollBox style={{border:'#FFFFFF', height: '200px'}} axes={ScrollAxes.Y} fastTrack={FastTrack.PAGING}>
                {items.map(({ id }) => (
                    <SceneCard itemId={id} />
                ))}
            </ScrollBox>
        </div>
    );
};
